// src/components/Chatbot.jsx
import React, { useState, useEffect } from 'react';
import { ChatGroq } from 'langchain-groq';
import { Button, Col, Row, DatePicker, TimePicker, Input, Select, Card, message, Typography, Radio, Space, Spin } from 'antd';
import MapComponent from './MapComponent';
import WeatherDisplay from './WeatherDisplay';
import ItineraryDisplay from './ItineraryDisplay';
import dayjs from 'dayjs';

const { TextArea } = Input;
const { Option } = Select;
const { Title } = Typography;

const Chatbot = () => {
  const [chatState, setChatState] = useState({
    step: 'city',
    city: '',
    startDate: dayjs(),
    endDate: dayjs().add(1, 'day'),
    duration: 1,
    arrivalTime: dayjs(),
    departureTime: dayjs(),
    travelingVia: 'vehicle',
    travelingWith: 'No',
    interests: [],
    suggestedLocations: []
  });

  const [itinerary, setItinerary] = useState(null);
  const [messages, setMessages] = useState([]);
  const [llm, setLlm] = useState(null);
  const [loading, setLoading] = useState(false);
  const [weatherData, setWeatherData] = useState(null);

  // Step Components
  const CityStep = () => (
    <Card title="üåç Enter your destination city">
      <Input
        value={chatState.city}
        onChange={(e) => setChatState({...chatState, city: e.target.value})}
        placeholder="Enter city name"
      />
      <Button 
        type="primary" 
        onClick={handleCityNext}
        disabled={!chatState.city}
        style={{ marginTop: 16 }}
      >
        Next
      </Button>
    </Card>
  );

  const DateStep = () => (
    <Card title="üìÖ Select your travel dates">
      <Row gutter={16}>
        <Col span={12}>
          <DatePicker
            value={chatState.startDate}
            onChange={(date) => setChatState({...chatState, startDate: date})}
            style={{ width: '100%' }}
          />
        </Col>
        <Col span={12}>
          <DatePicker
            value={chatState.endDate}
            onChange={(date) => setChatState({...chatState, endDate: date})}
            minDate={chatState.startDate}
            style={{ width: '100%' }}
          />
        </Col>
      </Row>
      <Button 
        type="primary" 
        onClick={handleDateNext}
        style={{ marginTop: 16 }}
      >
        Next
      </Button>
      <Button 
        onClick={() => setChatState({...chatState, step: 'city'})}
        style={{ marginLeft: 8 }}
      >
        Back
      </Button>
    </Card>
  );

  const TravelStep = () => (
    <Card title="üöó Travel Arrangements">
      <Radio.Group 
        value={chatState.travelingVia} 
        onChange={(e) => setChatState({...chatState, travelingVia: e.target.value})}
      >
        <Space direction="vertical">
          <Radio value="train">Train</Radio>
          <Radio value="flight">Flight</Radio>
          <Radio value="bus">Bus</Radio>
          <Radio value="vehicle">Personal Vehicle</Radio>
        </Space>
      </Radio.Group>
      <Button 
        type="primary" 
        onClick={() => setChatState({...chatState, step: 'times'})}
        style={{ marginTop: 16 }}
      >
        Next
      </Button>
      <Button 
        onClick={() => setChatState({...chatState, step: 'dates'})}
        style={{ marginLeft: 8 }}
      >
        Back
      </Button>
    </Card>
  );

  const TimeStep = () => (
    <Card title="‚è±Ô∏è Select Travel Times">
      <Row gutter={16}>
        <Col span={12}>
          <TimePicker
            value={chatState.arrivalTime}
            onChange={(time) => setChatState({...chatState, arrivalTime: time})}
            format="h:mm a"
            style={{ width: '100%' }}
          />
        </Col>
        <Col span={12}>
          <TimePicker
            value={chatState.departureTime}
            onChange={(time) => setChatState({...chatState, departureTime: time})}
            format="h:mm a"
            style={{ width: '100%' }}
          />
        </Col>
      </Row>
      <Button 
        type="primary" 
        onClick={() => setChatState({...chatState, step: 'travelingWith'})}
        style={{ marginTop: 16 }}
      >
        Next
      </Button>
      <Button 
        onClick={() => setChatState({...chatState, step: 'travelingVia'})}
        style={{ marginLeft: 8 }}
      >
        Back
      </Button>
    </Card>
  );

  const TravelingWithStep = () => (
    <Card title="üë• Traveling With">
      <Radio.Group 
        value={chatState.travelingWith} 
        onChange={(e) => setChatState({...chatState, travelingWith: e.target.value})}
      >
        <Space direction="vertical">
          <Radio value="Yes">Yes</Radio>
          <Radio value="No">No</Radio>
        </Space>
      </Radio.Group>
      <Button 
        type="primary" 
        onClick={() => setChatState({...chatState, step: 'interests'})}
        style={{ marginTop: 16 }}
      >
        Next
      </Button>
      <Button 
        onClick={() => setChatState({...chatState, step: 'times'})}
        style={{ marginLeft: 8 }}
      >
        Back
      </Button>
    </Card>
  );

  const InterestsStep = () => (
    <Card title="üéØ Your Interests">
      <TextArea
        value={chatState.interests.join(', ')}
        onChange={(e) => setChatState({
          ...chatState, 
          interests: e.target.value.split(',').map(i => i.trim())
        })}
        placeholder="Enter your interests, separated by commas"
        autoSize={{ minRows: 3, maxRows: 5 }}
      />
      <Button 
        type="primary" 
        onClick={() => setChatState({...chatState, step: 'confirm'})}
        style={{ marginTop: 16 }}
      >
        Next
      </Button>
      <Button 
        onClick={() => setChatState({...chatState, step: 'travelingWith'})}
        style={{ marginLeft: 8 }}
      >
        Back
      </Button>
    </Card>
  );

  const ConfirmStep = () => (
    <Card title="‚úÖ Confirm Your Trip">
      <div className="trip-summary">
        <p><strong>üåç Destination:</strong> {chatState.city}</p>
        <p><strong>üìÖ Dates:</strong> {chatState.startDate.format('MMM D, YYYY')} - {chatState.endDate.format('MMM D, YYYY')}</p>
        <p><strong>‚è±Ô∏è Duration:</strong> {chatState.duration} days</p>
        <p><strong>üöó Traveling via:</strong> {chatState.travelingVia}</p>
        <p><strong>üõ¨ Arrival Time:</strong> {chatState.arrivalTime.format('h:mm a')}</p>
        <p><strong>üõ´ Departure Time:</strong> {chatState.departureTime.format('h:mm a')}</p>
        <p><strong>üë• Traveling with pets/children:</strong> {chatState.travelingWith}</p>
        <p><strong>üéØ Interests:</strong> {chatState.interests.join(', ')}</p>
      </div>
      <Button 
        type="primary" 
        onClick={handleGenerateItinerary}
        style={{ marginTop: 16 }}
      >
        Generate Itinerary
      </Button>
      <Button 
        onClick={() => setChatState({...chatState, step: 'interests'})}
        style={{ marginLeft: 8 }}
      >
        Back
      </Button>
    </Card>
  );

  // Step Handlers
  const handleCityNext = async () => {
    setLoading(true);
    try {
      const weather = await getWeatherForecast(chatState.city);
      setWeatherData(weather);
      setChatState({...chatState, step: 'dates'});
    } catch (error) {
      message.error('Failed to fetch weather data');
    } finally {
      setLoading(false);
    }
  };

  const handleDateNext = () => {
    const duration = chatState.endDate.diff(chatState.startDate, 'day') + 1;
    setChatState({...chatState, duration, step: 'travelingVia'});
  };

  const handleGenerateItinerary = async () => {
    setLoading(true);
    try {
      const itinerary = await generateItinerary();
      setItinerary(itinerary);
      setChatState({...chatState, step: 'itinerary'});
    } catch (error) {
      message.error('Failed to generate itinerary');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="chatbot-container">
      <Title level={2}>üåç ChaloChalein</Title>
      {loading && <Spin size="large" />}
      
      <Row gutter={16}>
        <Col span={16}>
          {chatState.step === 'city' && <CityStep />}
          {chatState.step === 'dates' && <DateStep />}
          {chatState.step === 'travelingVia' && <TravelStep />}
          {chatState.step === 'times' && <TimeStep />}
          {chatState.step === 'travelingWith' && <TravelingWithStep />}
          {chatState.step === 'interests' && <InterestsStep />}
          {chatState.step === 'confirm' && <ConfirmStep />}
          {chatState.step === 'itinerary' && (
            <ItineraryDisplay 
              itinerary={itinerary}
              onEdit={handleEditItinerary}
              onSuggestMore={handleSuggestMore}
            />
          )}
        </Col>
        
        <Col span={8}>
          {weatherData && <WeatherDisplay data={weatherData} />}
          <MapComponent 
            destination={chatState.city}
            locations={[
              ...(itinerary?.dailyPlans || []),
              ...(chatState.suggestedLocations || [])
            ]}
          />
        </Col>
      </Row>
    </div>
  );
};

export default Chatbot;
