// src/components/Chatbot.jsx
import React, { useState, useEffect } from 'react';
import { Button, Col, Row, DatePicker, TimePicker, Input, Select, Card, message, Typography, Radio, Space, Spin } from 'antd';
import { useNavigate } from 'react-router-dom';
import MapComponent from './MapComponent';
import axios from 'axios';
import dayjs from 'dayjs';

const { TextArea } = Input;
const { Option } = Select;
const { Title } = Typography;

const API_URL = 'http://localhost:8000/api';

const Chatbot = () => {
  const navigate = useNavigate();
  const [chatState, setChatState] = useState({
    step: 'city',
    city: '',
    startDate: dayjs(),
    endDate: dayjs().add(1, 'day'),
    duration: 1,
    arrivalTime: dayjs(),
    departureTime: dayjs(),
    travelingVia: 'vehicle',
    travelingWith: 'No',
    interests: [],
    suggestedLocations: []
  });

  const [itinerary, setItinerary] = useState(null);
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [weatherData, setWeatherData] = useState(null);
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    // Check if user is logged in
    const user = localStorage.getItem('user');
    if (!user) {
      message.warning('Please login first');
      navigate('/auth');
    }
  }, [navigate]);

  const addMessage = (text, isUser = false) => {
    setMessages(prev => [...prev, { text, isUser, timestamp: new Date() }]);
  };

  const handleCitySubmit = async () => {
    if (!chatState.city) {
      message.error('Please enter a city');
      return;
    }

    addMessage(`I want to plan a trip to ${chatState.city}`, true);
    addMessage(`Great! When are you planning to visit ${chatState.city}?`);

    // Fetch weather data
    try {
      setLoading(true);
      const response = await axios.post(`${API_URL}/weather`, {
        city: chatState.city
      });

      setWeatherData(response.data);

      // Suggest locations
      const locationsResponse = await axios.post(`${API_URL}/locations/suggest`, {
        city: chatState.city
      });

      setLocations(locationsResponse.data.locations);
      setChatState(prev => ({
        ...prev,
        step: 'dates',
        suggestedLocations: locationsResponse.data.locations
      }));
    } catch (error) {
      console.error('Error fetching data:', error);
      message.error('Failed to fetch data for this city');
    } finally {
      setLoading(false);
    }
  };

  const handleDatesSubmit = () => {
    const startDateStr = chatState.startDate.format('YYYY-MM-DD');
    const endDateStr = chatState.endDate.format('YYYY-MM-DD');

    addMessage(`I'll be visiting from ${startDateStr} to ${endDateStr}`, true);
    addMessage('How will you be traveling?');

    setChatState(prev => ({
      ...prev,
      step: 'travel',
      duration: chatState.endDate.diff(chatState.startDate, 'day') + 1
    }));
  };

  const handleTravelSubmit = () => {
    addMessage(`I'll be traveling by ${chatState.travelingVia}`, true);
    addMessage('What are your interests for this trip?');

    setChatState(prev => ({
      ...prev,
      step: 'interests'
    }));
  };

  const handleInterestsSubmit = async () => {
    if (chatState.interests.length === 0) {
      message.error('Please select at least one interest');
      return;
    }

    addMessage(`My interests are: ${chatState.interests.join(', ')}`, true);
    addMessage('Generating your personalized itinerary...');

    try {
      setLoading(true);
      const response = await axios.post(`${API_URL}/itinerary`, {
        city: chatState.city,
        startDate: chatState.startDate.format('YYYY-MM-DD'),
        endDate: chatState.endDate.format('YYYY-MM-DD'),
        interests: chatState.interests,
        travelingVia: chatState.travelingVia
      });

      setItinerary(response.data.itinerary);
      addMessage('Here is your personalized itinerary!');

      setChatState(prev => ({
        ...prev,
        step: 'itinerary'
      }));
    } catch (error) {
      console.error('Error generating itinerary:', error);
      message.error('Failed to generate itinerary');
    } finally {
      setLoading(false);
    }
  };

  const handleEditItinerary = () => {
    message.info('Edit functionality coming soon!');
  };

  const handleSuggestMore = async () => {
    try {
      setLoading(true);
      const response = await axios.post(`${API_URL}/locations/suggest`, {
        city: chatState.city,
        query: chatState.interests.join(' ')
      });

      setLocations(prev => [...prev, ...response.data.locations]);
      message.success('More locations added to the map!');
    } catch (error) {
      console.error('Error suggesting more locations:', error);
      message.error('Failed to suggest more locations');
    } finally {
      setLoading(false);
    }
  };

  const renderChatStep = () => {
    switch (chatState.step) {
      case 'city':
        return (
          <div>
            <Title level={4}>Where would you like to go?</Title>
            <Input 
              placeholder="Enter city name" 
              value={chatState.city}
              onChange={e => setChatState(prev => ({ ...prev, city: e.target.value }))}
              style={{ marginBottom: 16 }}
            />
            <Button type="primary" onClick={handleCitySubmit}>Next</Button>
          </div>
        );

      case 'dates':
        return (
          <div>
            <Title level={4}>When are you planning to visit?</Title>
            <Space direction="vertical" size={16} style={{ width: '100%' }}>
              <DatePicker 
                placeholder="Start Date"
                value={chatState.startDate}
                onChange={date => setChatState(prev => ({ ...prev, startDate: date }))}
                style={{ width: '100%' }}
              />
              <DatePicker 
                placeholder="End Date"
                value={chatState.endDate}
                onChange={date => setChatState(prev => ({ ...prev, endDate: date }))}
                style={{ width: '100%' }}
              />
              <Button type="primary" onClick={handleDatesSubmit}>Next</Button>
            </Space>
          </div>
        );

      case 'travel':
        return (
          <div>
            <Title level={4}>How will you be traveling?</Title>
            <Radio.Group 
              value={chatState.travelingVia}
              onChange={e => setChatState(prev => ({ ...prev, travelingVia: e.target.value }))}
              style={{ marginBottom: 16 }}
            >
              <Space direction="vertical">
                <Radio value="vehicle">By Vehicle</Radio>
                <Radio value="public_transport">Public Transport</Radio>
                <Radio value="walking">Walking</Radio>
              </Space>
            </Radio.Group>
            <Button type="primary" onClick={handleTravelSubmit}>Next</Button>
          </div>
        );

      case 'interests':
        return (
          <div>
            <Title level={4}>What are your interests?</Title>
            <Select
              mode="multiple"
              placeholder="Select interests"
              value={chatState.interests}
              onChange={values => setChatState(prev => ({ ...prev, interests: values }))}
              style={{ width: '100%', marginBottom: 16 }}
            >
              <Option value="history">History</Option>
              <Option value="art">Art</Option>
              <Option value="food">Food</Option>
              <Option value="nature">Nature</Option>
              <Option value="adventure">Adventure</Option>
              <Option value="shopping">Shopping</Option>
              <Option value="nightlife">Nightlife</Option>
              <Option value="culture">Culture</Option>
            </Select>
            <Button type="primary" onClick={handleInterestsSubmit}>Generate Itinerary</Button>
          </div>
        );

      case 'itinerary':
        return (
          <div>
            <Title level={4}>Your Personalized Itinerary</Title>
            {itinerary && (
              <div>
                {itinerary.map((day, index) => (
                  <Card 
                    key={index} 
                    title={`Day ${day.day}: ${day.date}`}
                    style={{ marginBottom: 16 }}
                  >
                    {day.activities.map((activity, actIndex) => (
                      <div key={actIndex} style={{ marginBottom: 8 }}>
                        <strong>{activity.time}</strong>: {activity.description} at {activity.location}
                      </div>
                    ))}
                  </Card>
                ))}
                <Space>
                  <Button onClick={handleEditItinerary}>Edit Itinerary</Button>
                  <Button onClick={handleSuggestMore}>Suggest More Places</Button>
                </Space>
              </div>
            )}
          </div>
        );

      default:
        return <div>Something went wrong</div>;
    }
  };

  return (
    <div className="chatbot-container">
      <Row gutter={16}>
        <Col span={16}>
          <Card title="Plan Your Trip" style={{ marginBottom: 16 }}>
            {loading ? (
              <div style={{ textAlign: 'center', padding: 24 }}>
                <Spin size="large" />
                <div style={{ marginTop: 16 }}>Processing your request...</div>
              </div>
            ) : (
              renderChatStep()
            )}
          </Card>

          <Card title="Chat History">
            <div className="chat-messages">
              {messages.map((msg, index) => (
                <div 
                  key={index} 
                  className={`message ${msg.isUser ? 'user-message' : 'bot-message'}`}
                >
                  {msg.text}
                </div>
              ))}
            </div>
          </Card>
        </Col>

        <Col span={8}>
          {weatherData && (
            <Card title="Weather" style={{ marginBottom: 16 }}>
              <div>
                <div><strong>Location:</strong> {weatherData.location}</div>
                <div><strong>Temperature:</strong> {weatherData.temperature}Â°C</div>
                <div><strong>Condition:</strong> {weatherData.description}</div>
              </div>
            </Card>
          )}

          {locations.length > 0 && (
            <Card title="Map">
              <MapComponent 
                locations={locations.map(loc => ({
                  name: loc.name,
                  position: [loc.latitude, loc.longitude]
                }))}
                center={[locations[0].latitude, locations[0].longitude]}
              />
            </Card>
          )}
        </Col>
      </Row>

      <style jsx>{`
        .chatbot-container {
          padding: 24px;
        }
        .chat-messages {
          max-height: 300px;
          overflow-y: auto;
        }
        .message {
          padding: 8px 12px;
          margin-bottom: 8px;
          border-radius: 8px;
        }
        .user-message {
          background-color: #e6f7ff;
          margin-left: 20%;
        }
        .bot-message {
          background-color: #f0f0f0;
          margin-right: 20%;
        }
      `}</style>
    </div>
  );
};

export default Chatbot;
